import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import Header from "./components/Header";
import Expense from "./components/Expense";
import Table from "./components/Table";
import AddedExpense from "./components/AddedExpense";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      expenses: [],
      expense: {
        type: "",
        name: "",
        date: "",
        amount: ""
      }
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.addExpenseToList = this.addExpenseToList.bind(this);
  }

  handleChange(event) {
    console.log("changed");
    const { name, value } = event.target;
    const expense = { ...this.state.expense };
    expense[name] = value;
    console.log(name);
    console.log(value);
    this.setState({
      expense
    });
  }

  handleSubmit(event) {
    event.preventDefault();

    const expenseArray = this.state.expenses;
    //get updated version of state
    expenseArray.push(this.state.expense);
    this.setState({ expenses: expenseArray });
    console.log(expenseArray);
    console.log(this.state.expenses);
    this.addExpenseToList();
  }

  addExpenseToList(event) {
    console.log("expense added");
    //get expense array here
    const addedExpenses = this.state.expenses;
    //pull newest expense
    const newestExpense = addedExpenses.length - 1;
    //add it to the list

    return newestExpense;
  }

  render() {
    return (
      <div>
        <Header />
        <Expense
          expense={this.state.expense}
          handleChange={this.handleChange}
          handleSubmit={this.handleSubmit}
        />
        <Table />
        <AddedExpense
          expense={this.state.expense}
          addExpense={this.addExpenseToList}
          //handleChange={this.handleChange}
        />
      </div>
    );
  }
}

export default App;
